mapper.select:
SELECT user_id, name, sex, email, profile_img, user_type, create_time, last_edit_time, enable_status FROM tb_person_info
and user_type= #{personInfoCondition.userType}
and name like '%${personInfoCondition.name}%'
and enable_status = #{personInfoCondition.enableStatus}
ORDER BY create_time DESC LIMIT #{rowIndex},#{pageSize};

SELECT count(1) FROM tb_person_info
and user_type= #{personInfoCondition.userType}
and name like '%${personInfoCondition.name}%'
and enable_status = #{personInfoCondition.enableStatus}

mapper.update:
update tb_person_info
name=#{name}, sex=#{sex}, email=#{email}, user_type=#{profileImg}, admin_flag=#{userType}, last_edit_time=#{lastEditTime}, enable_status=#{enableStatus}
where user_id=#{userId}
